function loading_draw() {
	try {
		loading_context.clearRect(-25, -25, 50, 50);
		loading_context.rotate(Math.PI * 2 / spokes);
		for ( var i = 0; i < spokes; i++) {
			loading_context.rotate(Math.PI * 2 / spokes);
			loading_context.strokeStyle = "rgba(0,0,0," + i / spokes + ")";
			loading_context.beginPath();
			loading_context.moveTo(0, 10);
			loading_context.lineTo(0, 15);
			loading_context.stroke();
		}
	} catch (e) {
	}
}
