var cur_el = null;
function makeVodList(el) {
	var c = document.getElementById("content");
	cur_el = el;
	c.style.backgroundColor = "";
	c.innerHTML = "";
	loading_close(idle);
	oDiv = $c("div");
	oDiv.id = "html5vod";
	oDiv.style.cssText = "margin:0;padding:0;width:100%;height:431px;display:none;";
	c.appendChild(oDiv);
	oDiv = $c("div");
	oDiv.setAttribute("class", "lectureList");
	oDiv.setAttribute("className", "lectureList");
	oUl = $c("ul");
	oUl.id = "vl";
	oDiv.appendChild(oUl);
	c.appendChild(oDiv);
	hd = document.getElementById("tt");
	removeChildAll(hd);
	hd.appendChild($t(mList.ll[el.id].CATE_NAME));
	var hd = document.getElementById("l_n");
	removeChildAll(hd);
	oHref = $c("a");
	oHref.onclick = function() {
		var e = document.getElementById("content");
		e.innerHTML = "";
		idle = loading_start();
		makeCateList();
	};
	oImg = $c("img");
	oImg.src = "images/btn/b_home.png";
	oHref.appendChild(oImg);
	hd.appendChild(oHref);
	loadVod();
}
